"use strict";(self.webpackChunkmetaculus_doc=self.webpackChunkmetaculus_doc||[]).push([[53],{1109:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"link","label":"Basics","href":"/faq/","docId":"basics"},{"type":"category","label":"Metaculus Questions","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Creating Questions","href":"/faq/Metaculus Questions/creating-questions","docId":"Metaculus Questions/creating-questions"},{"type":"link","label":"Standard Definitions","href":"/faq/Metaculus Questions/standard-definitions","docId":"Metaculus Questions/standard-definitions"},{"type":"link","label":"Question Groups","href":"/faq/Metaculus Questions/question-groups","docId":"Metaculus Questions/question-groups"},{"type":"link","label":"Navigation and Filtering","href":"/faq/Metaculus Questions/navigation-and-filtering","docId":"Metaculus Questions/navigation-and-filtering"},{"type":"link","label":"Commenting Rules and Guidelines","href":"/faq/Metaculus Questions/commenting-rules","docId":"Metaculus Questions/commenting-rules"}]},{"type":"link","label":"Question Resolution","href":"/faq/question-resolution","docId":"question-resolution"},{"type":"category","label":"Predictions","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Making Predictions","href":"/faq/Predictions/making-predictions","docId":"Predictions/making-predictions"},{"type":"link","label":"Aggregating Predictions","href":"/faq/Predictions/aggregating-predictions","docId":"Predictions/aggregating-predictions"},{"type":"link","label":"Public Figure Predictions","href":"/faq/Predictions/public-figure-predictions","docId":"Predictions/public-figure-predictions"}]},{"type":"category","label":"Scoring","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Scoring Rules","href":"/faq/Scoring/scoring-rules","docId":"Scoring/scoring-rules"},{"type":"link","label":"Metaculus Points","href":"/faq/Scoring/metaculus-points","docId":"Scoring/metaculus-points"},{"type":"link","label":"Tournaments","href":"/faq/Scoring/tournaments","docId":"Scoring/tournaments"}]},{"type":"link","label":"Metaculus Journal","href":"/faq/metaculus-journal","docId":"metaculus-journal"},{"type":"link","label":"Forecasting Causes","href":"/faq/forecasting-causes","docId":"forecasting-causes"},{"type":"link","label":"Miscellany","href":"/faq/miscellany","docId":"miscellany"}]},"docs":{"basics":{"id":"basics","title":"Basics","description":"The Metaculus Platform","sidebar":"tutorialSidebar"},"forecasting-causes":{"id":"forecasting-causes","title":"Forecasting Causes","description":"About Forecasting Causes","sidebar":"tutorialSidebar"},"Metaculus Questions/commenting-rules":{"id":"Metaculus Questions/commenting-rules","title":"Commenting Rules and Guidelines","description":"We have a full set of community etiquette guidelines but in summary:","sidebar":"tutorialSidebar"},"Metaculus Questions/creating-questions":{"id":"Metaculus Questions/creating-questions","title":"Creating Questions","description":"How To Write Good Forecasting Questions","sidebar":"tutorialSidebar"},"Metaculus Questions/navigation-and-filtering":{"id":"Metaculus Questions/navigation-and-filtering","title":"Navigation and Filtering","description":"Questions on Metaculus are sorted by activity by default. Newer questions, questions with new comments, recently upvoted questions, and questions with many new predictions will appear at the top of the Metaculus homepage. However, there are several additional ways to find questions of interest and customize the way you interact with Metaculus.","sidebar":"tutorialSidebar"},"Metaculus Questions/question-groups":{"id":"Metaculus Questions/question-groups","title":"Question Groups","description":"Question groups are sets of closely related questions or question outcomes all collected on a single page. Forecasters can predict quickly and efficiently on these interconnected outcomes, confident that they are keeping all of their predictions internally consistent.","sidebar":"tutorialSidebar"},"Metaculus Questions/standard-definitions":{"id":"Metaculus Questions/standard-definitions","title":"Standard Definitions","description":"Credible Sources","sidebar":"tutorialSidebar"},"metaculus-journal":{"id":"metaculus-journal","title":"Metaculus Journal","description":"About the Metaculus Journal","sidebar":"tutorialSidebar"},"miscellany":{"id":"miscellany","title":"Miscellany","description":"Metaculus Pro Forecasters","sidebar":"tutorialSidebar"},"Predictions/aggregating-predictions":{"id":"Predictions/aggregating-predictions","title":"Aggregating Predictions","description":"Community Prediction","sidebar":"tutorialSidebar"},"Predictions/making-predictions":{"id":"Predictions/making-predictions","title":"Making Predictions","description":"You make a prediction simply by sliding the slider on the question\'s page to the probability you believe most captures the likelihood that the event will occur.","sidebar":"tutorialSidebar"},"Predictions/public-figure-predictions":{"id":"Predictions/public-figure-predictions","title":"Public Figure Predictions","description":"Public Figure Prediction pages are dedicated to collecting and preserving important predictions made by prominent public figures and putting them into conversation with Metaculus community forecasts. Each figure\u2019s page features a list of predictions they made along with the source that recorded the prediction, the date the prediction was made, and related Metaculus questions. Public predictions are transparently presented alongside community forecasts in a manner that is inspectable and understandable by all, providing public accountability and additional context for the linked Metaculus questions.","sidebar":"tutorialSidebar"},"question-resolution":{"id":"question-resolution","title":"Question Resolution","description":"Getting a Question Resolved","sidebar":"tutorialSidebar"},"Scoring/metaculus-points":{"id":"Scoring/metaculus-points","title":"Metaculus Points","description":"Overview","sidebar":"tutorialSidebar"},"Scoring/scoring-rules":{"id":"Scoring/scoring-rules","title":"Scoring Rules","description":"Brier Score","sidebar":"tutorialSidebar"},"Scoring/tournaments":{"id":"Scoring/tournaments","title":"Tournaments","description":"A forecasting tournament is a competition across one or multiple rounds of questions. Forecasters make their predictions on these questions, and once the questions are resolved, the forecasters are scored and ranked based on their accuracy. The top ranked forecasters can then receive payouts from a prize pool set aside for the tournament (in addition to other rewards like Metaculus hoodies), as well as the recognition that comes from winning the tournament. Metaculus tournaments use relative log scoring to award prizes, not Metaculus points.","sidebar":"tutorialSidebar"}}}')}}]);